// ArticleList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ArticleList.css'; // Create a separate CSS file for ArticleList
import Banner from './Banner';
import ArticleCard from './ArticleCard'; // Import the ArticleCard component
import { useUser } from './UserContext';
import { Link } from 'react-router-dom';


const ArticleList = () => {
    const [articles, setArticles] = useState([]);
    const [viewedArticles, setViewedArticles] = useState([]);
    const { user } = useUser();

    useEffect(() => {
        const fetchArticles = async () => {
            const response = await axios.post('http://localhost:5000/api/articles', user);
            setArticles(response.data);
        };
        fetchArticles();
        fetchViewedArticles();
    }, [user]);

    const fetchViewedArticles = async () => {
            const response = await  axios.post('http://localhost:5000/api/viewed-articles', user);
            const data = response.data;
      setViewedArticles(data);
  };
    return (
        <>
            <Banner />
            <div className="article-list">
                <div className="card-container list-header">
                    <h2>Recommended For You</h2>
                    <Link to="/allarticles" className="All-Articles">View All</Link>
                </div>
                <div className="card-container">
                    {articles.map(article => (
                        <ArticleCard key={article.id} article={article} /> // Use ArticleCard
                    ))}
                </div>
            </div>
            <div className="card-container list-header" style={{ display: viewedArticles.length > 0 ? 'block' : 'none' }}>
                    <h2>Your Viewed Articles</h2>
                </div>
                <div className="card-container">
                    {viewedArticles.map(article => (
                        <ArticleCard key={article.id} article={article} />
                    ))}
            </div>

        </>
    );
};

export default ArticleList;
