import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ArticleDetail.css'; // Create CSS for styling
import ArticleCard from './ArticleCard';
import { useUser } from './UserContext';


const ArticleDetail = () => {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const [relatedArticles, setRelatedArticles] = useState([]);
  const { user } = useUser();

    useEffect(() => {
      window.scrollTo(0, 0);
  }, [id]);

  useEffect(() => {
    const fetchArticle = async () => {
      const response = await axios.get(`http://localhost:5000/api/articles/${id}`, {
        params: {
          user
        }
      })
      
      setArticle(response.data);
      if(article.tag)
      fetchtag()
      fetchRelatedArticles()
    };
    
    fetchArticle();
  }, [id]);

  const fetchtag = async () => {
    const response = await axios.get(`http://localhost:5000/api/tagarticles/${article.tags}`, )
  }

  const fetchRelatedArticles = async () => {
    const response = await axios.get(`http://localhost:5000/api/articles/related/${id}`);
    setRelatedArticles(response.data);
  };

  const getVideoEmbedUrl = (videoUrl) => {
    const videoIdMatch = videoUrl.match(/embed\/([^?]+)/);
    const videoId = videoIdMatch ? videoIdMatch[1] : null;
    return videoId ? `https://www.youtube.com/embed/${videoId}` : null; 
  };

  if (!article) return <p>Loading...</p>;

  return (
    <>   
     <div className="article-detail">
      <h2>{article.title}</h2>
      {article.video_url ? (
        <iframe
          width="100%"
          height="315"
          src={getVideoEmbedUrl(article.video_url)} // Use the modified URL here
          title={article.title}
          frameBorder="0"
          allowFullScreen
        />
      ) : (
        <img 
          src={article.image_url || 'default_image.jpg'} 
          alt={article.title} 
        />
      )}
      <p>Duration: {article.duration} Minutes</p>
     
      <p>{article.description}</p>
      <p>Tags: {article.tags}</p>
      <p>Views: {article.count}</p>
      <div className={`tag ${article.difficulty}`}>{article.difficulty}</div>
      <div dangerouslySetInnerHTML={{ __html: article.content }} /> {/* Assuming `content` is in HTML format */}
    </div>


      <h3>Related Articles</h3>
        <div className="related-articles">
          {relatedArticles.map(relatedArticle => (
            <ArticleCard key={relatedArticle.id} article={relatedArticle} />
          ))}
        </div>
    </>

  );
};

export default ArticleDetail;
