// ArticleCard.js
import React from 'react';
import { Link } from 'react-router-dom';
import './ArticleCard.css'; 

const ArticleCard = ({ article }) => {
    const getThumbnailUrl = (videoUrl) => {
        const videoIdMatch = videoUrl.match(/embed\/([^?]+)/);
        const videoId = videoIdMatch ? videoIdMatch[1] : null;
        return videoId ? `https://img.youtube.com/vi/${videoId}/hqdefault.jpg` : 'default_image.jpg';
    };

    return (
        <div className="card">
            <img 
                src={article.image_url || (article.video_url ? getThumbnailUrl(article.video_url) : 'default_image.jpg')} 
                alt={article.title} 
            />
            <div className='card-info-contianer'>
                <h3>{article.title}</h3>
                <h4 className='by-author'>By <span className='author-card'>{article.author}</span></h4>
                <div className="duration-read-div">
                    <div className='duration'>
                        Duration: {article.duration} Minutes
                    </div>
                </div>
                <p>{article.description}</p>
                <span className={`tag ${article.difficulty}`}>{article.difficulty}</span>
                <Link to={`/articles/${article.id}`} className="read-more">View Course</Link>
            </div>
        </div>
    );  
};

export default ArticleCard;
