import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ArticleDetail.css'; // Create CSS for styling

const ArticleDetail = () => {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  
  useEffect(() => {
    const fetchArticle = async () => {
      const response = await axios.get(`http://localhost:5000/api/articles/${id}`);
      setArticle(response.data);
    };
    
    fetchArticle();
  }, [id]);

  const getThumbnailUrl = (videoUrl) => {
    const videoIdMatch = videoUrl.match(/embed\/([^?]+)/); // Extract the video ID from the embed URL
    const videoId = videoIdMatch ? videoIdMatch[1] : null; // Get the ID if it exists
    return videoId ? `https://img.youtube.com/vi/${videoId}/hqdefault.jpg` : 'default_image.jpg'; // Return thumbnail URL or default
  };
  
  if (!article) return <p>Loading...</p>;

  return (
    <div className="article-detail">
      <h2>{article.title}</h2>
      <img 
        src={article.image_url || (article.video_url ? getThumbnailUrl(article.video_url) : 'default_image.jpg')} 
        alt={article.title} 
        /> 
      <p>{article.description}</p>
      <div className={`tag ${article.difficulty}`}>{article.difficulty}</div>
      <div dangerouslySetInnerHTML={{ __html: article.content }} /> {/* Assuming `content` is in HTML format */}
    </div>
  );
};

export default ArticleDetail;
