import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AllArticles = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      const response = await axios.get('http://localhost:5000/api/articles/all');
      setArticles(response.data);
    };

    fetchArticles();
  }, []);

  const getThumbnailUrl = (videoUrl) => {
    const videoId = videoUrl.split('/').pop().split('?')[0]; // Extract the video ID
    console.log(videoId)
    return `https://img.youtube.com/vi/${videoId}/hqdefault.jpg`; // Construct thumbnail URL
  };

  return (
    <div className="all-articles">
      <h2>All Articles</h2>
      <div className="card-container">
        {articles.length > 0 ? (
          articles.map(article => (
            <div className="card" key={article.id}>
              <img 
                src={ (article.video_url ? getThumbnailUrl(article.video_url) : 'default_image.jpg')} 
                alt={article.title} 
              /> 
              <h3>{article.title}</h3>
              <p>{article.description}</p>
              <span className={`tag ${article.difficulty}`}>{article.difficulty}</span>
              <a href={`/articles/${article.id}`} className="read-more">Read More</a>
            </div>
          ))
        ) : (
          <p>Loading</p>
        )}
      </div>
    </div>
  );
};

export default AllArticles;
