import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ArticleList.css'; // Create a separate CSS file for ArticleList
import Banner from './Banner';

const ArticleList = () => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      const response = await axios.get('http://localhost:5000/api/articles');
      setArticles(response.data);
      console.log(response.data)
    };
    fetchArticles();
  }, []);

  return (
    <>
    <Banner />
    <div className="article-list">
      <h2>Articles</h2>
      <div className="card-container">
        {articles.map(article => (
          <div className="card" key={article.id}>
            <img src={article.image_url} alt={article.title} /> {/* Adjust based on your data structure */}
            <h3>{article.title}</h3>
            <p>{article.description}</p>
            <span className={`tag ${article.difficulty}`}>{article.difficulty}</span> {/* Adjust based on your data structure */}
            <a href={`/articles/${article.id}`} className="read-more">Read More</a>
          </div>
         
        ))}
      </div>
    </div>
    </>
  );
};

export default ArticleList;
