import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const SearchResults = () => {
  const [articles, setArticles] = useState([]);
  const location = useLocation();

  useEffect(() => {
    const fetchSearchResults = async () => {
      const query = new URLSearchParams(location.search).get('q'); // Get the search query from the URL
      const response = await axios.get(`http://localhost:5000/api/articles/search?q=${query}`);
      setArticles(response.data);
    };

    fetchSearchResults();
  }, [location]);

  return (
    <div className="search-results">
      <h2>Search Results</h2>
      <div className="card-container">
        {articles.length > 0 ? (
          articles.map(article => (
            <div className="card" key={article.id}>
              <img src={article.imageUrl} alt={article.title} />
              <h3>{article.title}</h3>
              <p>{article.description}</p>
              <span className={`tag ${article.difficulty}`}>{article.difficulty}</span>
              <a href={`/articles/${article.id}`} className="read-more">Read More</a>
            </div>
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </div>
    </div>
  );
};

export default SearchResults;
