import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ArticleList.css'; // Create a separate CSS file for ArticleList
import Banner from './Banner';
import { Link } from 'react-router-dom';
import { useUser } from './UserContext';

const ArticleList = () => {
  const [articles, setArticles] = useState([]);
  const { user } = useUser();
  console.log(user)

  useEffect(() => {
    const fetchArticles = async () => {
     
      const response = await axios.post('http://localhost:5000/api/articles', user.userId);
      setArticles(response.data);
    };
    fetchArticles();
  }, []);


  const getThumbnailUrl = (videoUrl) => {
    const videoIdMatch = videoUrl.match(/embed\/([^?]+)/); // Extract the video ID from the embed URL
    const videoId = videoIdMatch ? videoIdMatch[1] : null; // Get the ID if it exists
    return videoId ? `https://img.youtube.com/vi/${videoId}/hqdefault.jpg` : 'default_image.jpg'; // Return thumbnail URL or default
  };

  return (
    <>
    <Banner />
    <div className="article-list">
        <div className="card-container list-header">
            <h2>Courses</h2>
            <Link to="/allarticles"className="All-Articles">View All</Link>
        </div>
      
      <div className="card-container">
        {articles.map(article => (
          <div className="card" key={article.id}>
           <img 
                src={article.image_url || (article.video_url ? getThumbnailUrl(article.video_url) : 'default_image.jpg')} 
                alt={article.title} 
              /> 
            <h3>{article.title}</h3>
            <p>{article.description}</p>
            <span className={`tag ${article.difficulty}`}>{article.difficulty}</span> 
            <Link to={`/articles/${article.id}`} className="read-more">View Course</Link>
            
          </div>
         
        ))}
      </div>
    </div>
    </>
  );
};

export default ArticleList;
