import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext'; // Import user context
import './SearchBar.css'; // Ensure you import the CSS file

const SearchBar = () => {
  const { user } = useUser(); // Get user data from context
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      // Save the search query to the database
      await fetch('http://localhost:5000/api/save-query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user.id, query: searchTerm }),
      });


      navigate(`/search?q=${encodeURIComponent(searchTerm)}`); // Encode the search term for URL
    }
  };

  return (
    <form className="search-bar" onSubmit={handleSearch}>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search articles..."
      />
      <button type="submit">Search</button>
    </form>
  );
};

export default SearchBar;
