  }
  
  return (
    <div className="search-results">
      <h2>Search Results</h2>
      <div className="card-container">
        {articles.length > 0 ? (
          articles.map(article => (
            <ArticleCard key={article.id} article={article} /> 
          ))
        ) : (
          <p>No articles found.</p>
        )}
      </div>
    </div>
  );
};

export default SearchResults;
