import React, { useState , useEffect, useRef} from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useUser } from './UserContext'; // Import user context
import './SearchBar.css'; // Ensure you import the CSS file

const SearchBar = () => {
  const { user } = useUser(); // Get user data from context
  const [searchTerm, setSearchTerm] = useState('');
  const location = useLocation();
  const navigate = useNavigate();
  const formRef = useRef();
 

  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
   
  }, [user, navigate]);

  const handleSearch = async (e) => {
    e.preventDefault();
    
      
    if (location.pathname === '/search') {
      // If on search page, perform an AJAX call to show updates
      await fetch('http://localhost:5000/api/update-query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user.userId, query: searchTerm }),
      });
      
    } else {
      
      await fetch('http://localhost:5000/api/save-query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user.userId, query: searchTerm }),
      });
      
      setSearchTerm(''); // Reset the search term
      navigate(`/search?q=${encodeURIComponent(searchTerm)}`); // Navigate to search results
    }
  };

  return (
    <>
    <div className='search-container'>
      <div className='search-text-container'>
        <div className='search-text-container-title'>  {user && user.name ? `Welcome Back, ${user.name} !` : 'Welcome Back!' }</div>
        <div className='search-text-container-item'>Explore your courses and dive into new learning courses</div>
      </div>
      <form className="search-bar" ref={formRef} onSubmit={handleSearch}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) =>  setSearchTerm(e.target.value)}
          placeholder="Search Courses"
          id="searchbar"
        />
        <button className='submit-search' type="submit">
          <img src='/assets/images/search.svg' className='search-icon'></img>
        </button>
      </form>
    </div>
    </>
  );
};

export default SearchBar;
