import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useUser } from './UserContext'; 
import axios from 'axios';
import './SearchBar.css';

const SearchBar = () => {
  const { user } = useUser();
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const location = useLocation();
  const navigate = useNavigate();
  const formRef = useRef();

  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleSearch = async (e) => {
    e.preventDefault();

    if(user){
        await axios.post('http://localhost:5000/api/save-query', {
          userId: user.userId,
          query: searchTerm,
        });

        setSearchTerm('');
        setSuggestions([]);
        navigate(`/search?q=${encodeURIComponent(searchTerm)}`); 
      }
  };

  const fetchSuggestions = async (query) => {
    if (query.length === 0) {
      setSuggestions([]);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:5000/api/suggestions`, {
        params: { q: query },
      });
      setSuggestions(response.data.suggestions); 
    } catch (error) {
      console.error('Error fetching suggestions:', error);
    }
  };

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
    fetchSuggestions(e.target.value);
  };

  const handleSuggestionClick = (suggestion) => {
    handleSearch
    setSearchTerm(suggestion);
    setSuggestions([]);
  };

  return (
    <>
      <div className='search-container'>
        <div className='search-text-container'>
          <div className='search-text-container-title'>
            {user && user.name ? `Welcome Back, ${user.name}!` : 'Welcome Back!'}
          </div>
          <div className='search-text-container-item'>Explore your courses and dive into new learning courses</div>
        </div>
        <form className="search-bar" ref={formRef} onSubmit={handleSearch}>
          <input
            type="text"
            value={searchTerm}
            onChange={handleChange}
            placeholder="Search Courses"
            id="searchbar"
          />
          <button className='submit-search' type="submit">
            <img src='/assets/images/search.svg' className='search-icon' alt="Search" />
          </button>
        </form>
        {suggestions.length > 0 && (
          <ul className="suggestions-list">
            {suggestions.map((suggestion, index) => (
              <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
                {suggestion}
              </li>
            ))}
          </ul>
        )}
      </div>
    </>
  );
};

export default SearchBar;
