// ArticleList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ArticleList.css'; // Create a separate CSS file for ArticleList
import Banner from './Banner';
import ArticleCard from './ArticleCard'; // Import the ArticleCard component
import { useUser } from './UserContext';
import { Link } from 'react-router-dom';
import ViewedArticleCard from './ViewedArticleCard';


const ArticleList = () => {
    const [articles, setArticles] = useState([]);
    const [viewedArticles, setViewedArticles] = useState([]);
    const { user } = useUser();

    useEffect(() => {
        const fetchArticles = async () => {
            const response = await axios.post('http://localhost:5000/api/articles', user);
            setArticles(response.data);
        };
        fetchArticles();
        fetchViewedArticles();
    }, [user]);

    const fetchViewedArticles = async () => {
      const response = await  axios.post('http://localhost:5000/api/viewed-articles', user);
      const data = response.data;
      console.log(data)
      setViewedArticles(data);
  };
    return (
        <>
            <div className="card-container list-header padder " style={{ display: viewedArticles.length > 0 ? 'block' : 'none' }}>
                            <h2>Courses You Viewed</h2>
                            <Link></Link>
                        </div>
                <div className="card-container viewed-articles-div">
                   
                {viewedArticles.length > 0 ? (
                    viewedArticles.slice(0, 3).map(article => (
                        <ViewedArticleCard key={article.id} article={article} />
                    ))
                ) : (
                    <p>No viewed articles available.</p>
                )}
              </div>    
            <div className="article-list">
                <div className="list-header">
                    <h2>Courses you may like</h2>
                    {/* <Link to="/allarticles" className="All-Articles">View All</Link> */}
                </div>
                <div className="card-container">
                    {articles.map(article => (
                        <ArticleCard key={article.id} article={article} /> // Use ArticleCard
                    ))}
                </div>
            </div>
                

        </>
    );
};

export default ArticleList;
